    newSquares[i] = turn === "X" ? "O" : "X";

    setSquares(newSquares);
    setTurn(newSquares[i])



    console.log(i)


  }
  return (
    <>
      <h1>Tic Tac Toe</h1>
      <div className="board">
        {squares.map((square, index) => {
